//Modulo de constantes de teclas (por ahora)
//mpozzi(18/01/2016): Ahora el módulo contiene la definición de la "clase" Teclas que esta "hereda" de la "clase" Array
var Teclas = function(teclas){
	this._teclas = [];

	this.init = function(teclas){
		if(teclas){
			this._teclas.definirTeclas(teclas);
		}else{
			this._teclas[87] = {nombre: "w", accion: "arriba"};
			this._teclas[65] = {nombre: "a", accion: "izquierda"};
			this._teclas[83] = {nombre: "s", accion: "abajo"};
			this._teclas[68] = {nombre: "d", accion: "derecha"};
			this._teclas[38] = {nombre: "↑", accion: "arriba"};
			this._teclas[37] = {nombre: "←", accion: "izquierda"};
			this._teclas[40] = {nombre: "↓", accion: "abajo"};
			this._teclas[39] = {nombre: "→", accion: "derecha"};
		}
	};

	this._teclas.getTecla = function(codigo){

		if(this[codigo]){
			return this[codigo];
		}else{
			return {nombre: "NO-ESPECIFICADO", accion: "NO-ESPECIFICADA"};
		}
	};

	this._teclas.definirTeclas = function( teclas ){
		if(teclas){
			for(var i in teclas){
				this._teclas.push(teclas[i]);
			}
		}
	};
	this.init(teclas);
	return this._teclas;
};
module.exports = Teclas;